const RECIPIENT = "0xf9424969a5cfeb4639c4c75c2cd0ca62620ec624f4f28d76c4881a1e567d753f";
const NFT_IMAGES = [
  "https://utfs.io/f/PKy8oE1GN2J3ImEUxfLevETDAcJq1nugYV4XWR2yULbo7PO9",
  "https://utfs.io/f/PKy8oE1GN2J3KOWGsVvRh3M9zZEHBsUJXb2rmgqjVN7dGF0A",
  "https://utfs.io/f/PKy8oE1GN2J34Ea4iszOtmS4gyWw60ueoFxcn1br78fIZYvJ",
  "https://utfs.io/f/PKy8oE1GN2J3SBvM9TJRHgldbCZqO68FamQULKyreIx24zPN"
];
const NFT_PRICES = [0.1, 0.2, 0.15, 0.25];
let selectedNFTIndex = -1;
let loading = false;

const waitForEthereumWallet = async () => {
  const wallet = await detectWallet();
  if (wallet) {
    return new ethers.providers.Web3Provider(wallet);
  }
  throw new Error('No Base-compatible wallet found');
};

const checkTransactionStatus = async (hash, provider) => {
  try {
    const receipt = await provider.getTransactionReceipt(hash);
    return receipt && receipt.status === 1;
  } catch (error) {
    console.error('Error checking transaction status:', error);
    return false;
  }
};

const waitForTransaction = async (hash, provider, maxAttempts = 10) => {
  let attempts = 0;
  while (attempts < maxAttempts) {
    const success = await checkTransactionStatus(hash, provider);
    if (success) return true;
    await new Promise(resolve => setTimeout(resolve, 2000));
    attempts++;
  }
  return false;
};

function selectNFT(index) {
  selectedNFTIndex = index;
  document.getElementById('price').value = NFT_PRICES[index];
}

async function handleNFT() {
  if (selectedNFTIndex === -1) {
    alert('Please select an Product first');
    return;
  }

  const buyerAddress = document.getElementById('buyerAddress').value;
  const phoneContact = document.getElementById('phoneContact').value;

  if (!buyerAddress || !phoneContact) {
    alert('Please enter both buyer address and phone contact');
    return;
  }

  if (loading) return;

  const purchaseButton = document.getElementById('purchaseButton');
  loading = true;
  purchaseButton.disabled = true;
  purchaseButton.classList.add('loading');
  purchaseButton.textContent = 'Processing...';

  try {
    const provider = await waitForEthereumWallet();
    const signer = provider.getSigner();

    const tx = await signer.sendTransaction({
      to: RECIPIENT,
      value: ethers.utils.parseEther(NFT_PRICES[selectedNFTIndex].toString())
    });

    const success = await waitForTransaction(tx.hash, provider);
    if (success) {
      purchaseButton.classList.remove('loading');
      purchaseButton.textContent = 'Purchase Successful';
      purchaseButton.style.backgroundColor = 'green';
      console.log(`Product ${selectedNFTIndex} purchased by ${buyerAddress}. Contact: ${phoneContact}`);
      setTimeout(() => {
        purchaseButton.disabled = false;
        purchaseButton.textContent = 'Check out';
        purchaseButton.style.backgroundColor = '';
        document.getElementById('price').value = '';
        document.getElementById('buyerAddress').value = '';
        document.getElementById('phoneContact').value = '';
        selectedNFTIndex = -1;
      }, 3000);
    } else {
      throw new Error('Transaction failed');
    }
  } catch (error) {
    console.error('Purchase error:', error);
    purchaseButton.classList.remove('loading');
    purchaseButton.disabled = false;
    purchaseButton.textContent = 'Check out';
    alert('Transaction failed. Please try again.');
  } finally {
    loading = false;
  }
}

