  const ABI = [
    {
      "type": "function",
      "name": "payment",
      "inputs": [
        {
          "name": "addr",
          "type": "address",
          "internalType": "address payable"
        }
      ],
      "outputs": [],
      "stateMutability": "payable"
    }
  ];
  const PRODUCT_IMAGES = [
    "https://utfs.io/f/PKy8oE1GN2J3ImEUxfLevETDAcJq1nugYV4XWR2yULbo7PO9",
    "https://utfs.io/f/PKy8oE1GN2J3KOWGsVvRh3M9zZEHBsUJXb2rmgqjVN7dGF0A",
    "https://utfs.io/f/PKy8oE1GN2J34Ea4iszOtmS4gyWw60ueoFxcn1br78fIZYvJ",
    "https://utfs.io/f/PKy8oE1GN2J3SBvM9TJRHgldbCZqO68FamQULKyreIx24zPN"
  ];
  const PRODUCT_PRICES = [0.0001, 0.0002, 0.00015, 0.00025];

  let selectedProductIndex = -1;
  let provider = null;
  let isWalletConnected = false;
  let isLoading = false;

  const connectWallet = async () => {
    try {
      if (!window.ethereum) {
        alert('Please install a Web3 wallet like MetaMask!');
        return null;
      }

      provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send('eth_requestAccounts', []);

      const chainId = await window.ethereum.request({ method: 'eth_chainId' });
      console.log('Connected to chain:', chainId);

      if (chainId !== '0x4e454153')  {
        alert('Please connect to Aurora network');
        return null;
      }

      isWalletConnected = true;
      const purchaseButton = document.getElementById('purchaseButton');
      purchaseButton.textContent = 'Checkout';
      return provider;
    } catch (error) {
      console.error('Detailed wallet connection error:', error);
      alert(`Failed to connect wallet: ${error.message}`);
      return null;
    }
  };

  const sendPayment = async (amount, recipientAddress) => {
    try {
      const signer = provider.getSigner();
      const signerAddress = await signer.getAddress();
      console.log('Signer address:', signerAddress);

      const balance = await provider.getBalance(signerAddress);
      console.log('Account balance:', ethers.utils.formatEther(balance));

      const contract = new ethers.Contract(BASE_CONTRACT, ABI, signer);

      const amountWei = ethers.utils.parseEther(amount.toString());

      console.log('Attempting transaction with:', {
        recipient: recipientAddress,
        amount: amount,
        amountWei: amountWei.toString(),
        contractAddress: BASE_CONTRACT
      });

      try {
        const code = await provider.getCode(BASE_CONTRACT);
        if (code === '0x') {
          throw new Error('No contract code found at the specified address');
        }
        console.log('Contract code verified');
      } catch (error) {
        console.error('Contract verification failed:', error);
        throw new Error('Could not verify contract. Please check the network and address.');
      }

      let gasEstimate;
      try {
        gasEstimate = await contract.estimateGas.payment(recipientAddress, { 
          value: amountWei 
        });
        console.log('Estimated gas:', gasEstimate.toString());
      } catch (gasError) {
        console.error('Gas estimation failed:', gasError);
        throw new Error('Failed to estimate gas. The transaction may fail.');
      }

      const tx = await contract.payment(recipientAddress, { 
        value: amountWei,
        gasLimit: gasEstimate.mul(120).div(100)
      });

      console.log('Transaction hash:', tx.hash);
      const receipt = await tx.wait();
      console.log('Transaction receipt:', receipt);

      return true;
    } catch (error) {
      console.error('Detailed transaction error:', error);
      if (error.data) {
        console.error('Error data:', error.data);
      }
      alert(`Transaction failed: ${error.message}`);
      return false;
    }
  };

  const handleProduct = async () => {
    if (isLoading) return;

    const purchaseButton = document.getElementById('purchaseButton');
    const buyerAddress = document.getElementById('buyerAddress').value;
    const phoneContact = document.getElementById('phoneContact').value;

    if (selectedProductIndex === -1) {
      alert('Please select a Product first');
      return;
    }

    if (!buyerAddress || !phoneContact) {
      alert('Please enter both shipping address and phone contact');
      return;
    }

    if (!isWalletConnected) {
      const connected = await connectWallet();
      if (!connected) return;
    }

    const amount = PRODUCT_PRICES[selectedProductIndex];
    const recipientAddress = RECIPIENT;

    isLoading = true;
    purchaseButton.disabled = true;
    purchaseButton.classList.add('loading');
    purchaseButton.textContent = 'Processing...';

    try {
      const success = await sendPayment(amount, recipientAddress);

      if (success) {
        purchaseButton.classList.remove('loading');
        purchaseButton.textContent = 'Purchase Successful';
        purchaseButton.style.backgroundColor = 'green';
        console.log(`Product ${selectedProductIndex} purchased by ${buyerAddress}. Contact: ${phoneContact}`);
        setTimeout(() => {
          purchaseButton.disabled = false;
          purchaseButton.textContent = 'Checkout';
          purchaseButton.style.backgroundColor = '';
          document.getElementById('buyerAddress').value = '';
          document.getElementById('phoneContact').value = '';
          selectedProductIndex = -1;
          document.querySelectorAll('.image').forEach(img => img.classList.remove('selected'));
        }, 3000);
      }
    } catch (error) {
      console.error('Purchase error:', error);
      purchaseButton.classList.remove('loading');
      purchaseButton.disabled = false;
      purchaseButton.textContent = 'Checkout';
      alert('Transaction failed. Please try again.');
    } finally {
      isLoading = false;
    }
  };

  const selectProduct = (index) => {
    selectedProductIndex = index;
    document.querySelectorAll('.image').forEach((img, i) => {
      if (i === index) {
        img.classList.add('selected');
      } else {
        img.classList.remove('selected');
      }
    });
    console.log('Selected product index:', selectedProductIndex);
  };

  const initializeMarketplace = () => {
    const purchaseButton = document.getElementById('purchaseButton');
    purchaseButton.addEventListener('click', handleProduct);

    document.querySelectorAll('.image').forEach(img => {
      img.addEventListener('click', () => {
        const index = parseInt(img.getAttribute('data-index'), 10);
        selectProduct(index);
      });
    });
  };

  // Call initializeMarketplace when the DOM is fully loaded
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initializeMarketplace);
  } else {
    initializeMarketplace();
  }
})();

