    if (isSubmitting) return;

    const userName = document.getElementById('userName').value;
    const reviewMessage = document.getElementById('reviewMessage').value;
    const submitButton = document.getElementById('submitButton');

    if (!userName || !reviewMessage) {
      alert('Please fill in all fields.');
      return;
    }

    isSubmitting = true;
    submitButton.disabled = true;
    submitButton.classList.add('sending');
    submitButton.textContent = 'Sending...';

    try {
      const review = { userName, reviewMessage, timestamp: new Date().toISOString() };
      const response = await fetch(`https://api.cloudflare.com/client/v4/accounts/${CLOUDFLARE_ACCOUNT_ID}/storage/kv/namespaces/${CLOUDFLARE_NAMESPACE_ID}/values/reviews`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${CLOUDFLARE_API_TOKEN}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(review),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      submitButton.classList.remove('sending');
      submitButton.classList.add('sent');
      submitButton.textContent = 'Thank you for your review!';

      document.getElementById('userName').value = '';
      document.getElementById('reviewMessage').value = '';

      setTimeout(() => {
        submitButton.disabled = false;
        submitButton.classList.remove('sent');
        submitButton.textContent = 'Send Review';
        isSubmitting = false;
      }, 3000);
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('Failed to submit review. Please try again.');
      submitButton.disabled = false;
      submitButton.classList.remove('sending');
      submitButton.textContent = 'Send Review';
