(function() {
const contractAddress = "0x0466A29D90D95365E98FC477AD39D7c00E31Dc3C";
const contractABI = []; // Add your contract ABI here
const name = "Voting";
const creator = "0x2";

let candidates = [];
let scores = [];
let timer = 300;
let isInitialized = false;

const waitForWeb3 = () => {
return new Promise((resolve) => {
const checkWeb3 = () => {
if (window.ethereum) {
resolve(new Web3(window.ethereum));
} else {
setTimeout(checkWeb3, 100);
}
};
checkWeb3();
});
};

const showError = (message) => {
alert(message);
};

const checkTransactionStatus = async (hash, web3) => {
try {
const receipt = await web3.eth.getTransactionReceipt(hash);
return receipt && receipt.status;
} catch (error) {
console.error('Error checking transaction status:', error);
return false;
}
};

const waitForTransaction = async (hash, web3, maxAttempts = 10) => {
for (let i = 0; i < maxAttempts; i++) {
const success = await checkTransactionStatus(hash, web3);
if (success) return true;
await new Promise(resolve => setTimeout(resolve, 1000));
}
return false;
};

const fetchScores = async (web3) => {
try {
const contract = new web3.eth.Contract(contractABI, contractAddress);
const result = await contract.methods.viewCurrentScores(creator).call();
return result;
} catch (error) {
console.error('Error fetching scores:', error);
showError('Failed to fetch current scores');
return null;
}
};

const updateCandidatesAndScores = async (web3) => {
const result = await fetchScores(web3);
if (result && Array.isArray(result) && result.length === 2) {
candidates = result[0];
scores = result[1].map(score => parseInt(score));
renderCandidates();
}
};

const renderCandidates = () => {
const candidateList = document.getElementById('candidateList');
if (!candidateList) return;

candidateList.innerHTML = '';
candidates.forEach((candidate, index) => {
const candidateItem = document.createElement('div');
candidateItem.className = 'candidate-item';
candidateItem.innerHTML = `<span>${candidate}</span><span>${scores[index]} votes</span>`;
candidateList.appendChild(candidateItem);
});
};

const updateTimer = () => {
const timerElement = document.getElementById('timer');
if (!timerElement) return;
timerElement.textContent = `Time remaining: ${formatTime(timer)}`;
};

const formatTime = (time) => {
const minutes = Math.floor(time / 60);
const seconds = time % 60;
return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
};

const handleVote = async (selectedCandidate, web3) => {
const voteButton = document.getElementById('voteButton');
if (!voteButton) return;

try {
const accounts = await web3.eth.getAccounts();
const account = accounts[0];

const contract = new web3.eth.Contract(contractABI, contractAddress);

voteButton.disabled = true;
voteButton.classList.add('loading');
voteButton.textContent = 'Voting...';

const gas = await contract.methods.vote(selectedCandidate, creator).estimateGas({ from: account });
const transaction = await contract.methods.vote(selectedCandidate, creator).send({ from: account, gas });

const success = await waitForTransaction(transaction.transactionHash, web3);

if (success) {
voteButton.textContent = 'Vote Successful';
await updateCandidatesAndScores(web3);

setTimeout(() => {
voteButton.classList.remove('loading');
voteButton.disabled = false;
voteButton.textContent = 'Vote';
}, 3000);
} else {
throw new Error('Vote failed');
}
} catch (error) {
console.error('Voting error:', error);
voteButton.classList.remove('loading');
voteButton.disabled = false;
voteButton.textContent = 'Vote';
showError('Vote failed. Please try again.');
}
};

const handleDeclareWinner = async (web3) => {
const declareButton = document.getElementById('declareWinnerButton');
if (!declareButton) return;

if (timer > 0) {
showError('Voting is still in progress');
return;
}

try {
const accounts = await web3.eth.getAccounts();
const account = accounts[0];

const contract = new web3.eth.Contract(contractABI, contractAddress);

declareButton.disabled = true;
declareButton.classList.add('loading');
declareButton.textContent = 'Declaring...';

const gas = await contract.methods.declareWinner(creator).estimateGas({ from: account });
const transaction = await contract.methods.declareWinner(creator).send({ from: account, gas });

const success = await waitForTransaction(transaction.transactionHash, web3);

if (success) {
const winnerIndex = scores.indexOf(Math.max(...scores));
showError(`Winner: ${candidates[winnerIndex]} with ${scores[winnerIndex]} votes!`);

setTimeout(() => {
declareButton.classList.remove('loading');
declareButton.disabled = false;
declareButton.textContent = 'Declare Winner';
}, 3000);
} else {
throw new Error('Failed to declare winner');
}
} catch (error) {
console.error('Error declaring winner:', error);
declareButton.classList.remove('loading');
declareButton.disabled = false;
declareButton.textContent = 'Declare Winner';
showError('Failed to declare winner');
}
};

const initializeVoting = async () => {
const web3 = await waitForWeb3();
const voteButton = document.getElementById('voteButton');
const declareButton = document.getElementById('declareWinnerButton');

if (voteButton) {
voteButton.addEventListener('click', () => {
const selectedCandidate = prompt(`Enter the candidate name to vote (options: ${candidates.join(', ')}):`); 
if (!selectedCandidate) return;

if (!candidates.includes(selectedCandidate)) {
showError('Invalid option selected');
return;
}

handleVote(selectedCandidate, web3);
});
}

if (declareButton) {
declareButton.addEventListener('click', () => handleDeclareWinner(web3));
}

await updateCandidatesAndScores(web3);

setInterval(() => {
if (timer > 0) {
timer--;
updateTimer();
}
}, 1000);
};

initializeVoting().catch(error => {
console.error('Failed to initialize voting:', error);
showError('Failed to initialize voting system');
});
})()
