    wallOfLoveLink: 'https://example.com/wall-of-love'
  }
};

function initializeTemplate() {
  document.getElementById('profileImage').src = CONFIG.profile.image;
  document.getElementById('profileName').textContent = CONFIG.profile.name;
  document.getElementById('profileBio').textContent = CONFIG.profile.bio;
  document.getElementById('wallOfLoveLink').href = CONFIG.profile.wallOfLoveLink;

  const stars = document.querySelectorAll('.star');
  stars.forEach(star => {
    star.addEventListener('click', setRating);
  });
}

let currentRating = 0;

function setRating(e) {
  const rating = e.target.getAttribute('data-rating');
  currentRating = rating;
  updateStars();
}

function updateStars() {
  const stars = document.querySelectorAll('.star');
  stars.forEach(star => {
    star.classList.toggle('active', star.getAttribute('data-rating') <= currentRating);
  });
}

function submitReview() {
  const userName = document.getElementById('userName').value;
  const reviewMessage = document.getElementById('reviewMessage').value;
  const submitButton = document.getElementById('submitButton');

  if (!userName || !reviewMessage || currentRating === 0) {
    alert('Please fill in all fields and select a rating.');
    return;
  }

  // Disable button and show sending state
  submitButton.disabled = true;
  submitButton.classList.add('sending');
  submitButton.textContent = 'Sending...';

  // Simulate sending data to server
  setTimeout(() => {
    // Here you would typically send this data to your server
    console.log('Review submitted:', { userName, reviewMessage, rating: currentRating });

    // Show sent state
    submitButton.classList.remove('sending');
    submitButton.classList.add('sent');
    submitButton.textContent = 'Thank you for your review!';

    // Clear the form
    document.getElementById('userName').value = '';
    document.getElementById('reviewMessage').value = '';
    currentRating = 0;
    updateStars();

    // Reset button after 3 seconds
    setTimeout(() => {
      submitButton.disabled = false;
      submitButton.classList.remove('sent');
      submitButton.textContent = 'Send Review';
    }, 3000);
  }, 1500); // Simulate network delay
}

document.addEventListener('DOMContentLoaded', initializeTemplate);

